union.
function Alert({
  level,
  children,
}: {
  level: "info" | "warn";
  children: ReactNode;
}) {
  const iconName = { info: "info", warn: "alert-triangle" }[level];
  return (
    <View>
      <Feather name={iconName} />
      {children}
    </View>
  );
}
